mapper.select:
select *
  from store
 order by store_no desc limit #{skip}, #{size}

mapper.update:
update store
   set store_name = #{sname}, store_addr = #{saddr}, store_tel = #{stel}, store_category = #{category},
   	   store_opentime = #{openTime}, store_closetime = #{closeTime}, store_lat = #{lat}, store_lng = #{lng}
 where store_no = #{sno}
